import React, { Component, Fragment } from 'react';

import axios from 'axios'

class Product extends Component {
    constructor(props){
        super(props);

        this.state = {
            id: props.match.params.id,
            loading: false,
            data: {}
        };
    }

    componentDidMount(){
        axios.get(`https://api.mercadolibre.com/items/${this.state.id}`)
            .then(({data}) => {
                this.setState({
                    data,
                })
            });
    }


    render(){
        //console.log(this.state.data)
        const { data } = this.state
        if (!Object.keys(data).length){
            return (
                <div style={{ fontSize: 40 }}>Loading...</div>
            );
        }

        return (
            <Fragment>
                <div className="mdl-card">

                    <div className="mdl-grid">
                        <div class="mdl-cell mdl-cell--6-col mdl-cell--8-col-tablet">
                            <img src={ data.pictures[0].url } />
                        </div>

                        <div class="mdl-cell mdl-cell--6-col mdl-cell--8-col-tablet">
                            <h1 className="mdl-card__title">{ data.title }</h1>

                            <button type="button" className="mdl-button mdl-js-button mdl-button--raised mdl-button--colored">
                                Comprar
                            </button>
                        </div>
                    </div>



                </div>

            </Fragment>
        );
    }
}

export default Product;
